package org.ethereum.core;

import org.junit.Ignore;
import org.junit.Test;
import org.spongycastle.util.encoders.Hex;

import java.math.BigInteger;
import java.util.ArrayList;
import java.util.Collections;

import static org.junit.Assert.*;

/**
 * @author Mikhail Kalinin
 * @since 31.08.2015
 */
public class PowHelperTest {

    @Test // valid block
    public void test_1() {
        byte[] rlp = Hex.decode("");
        Block b = new Block(rlp);
        assertTrue(PowHelper.isValid(b.getHeader()));
    }

    @Test // invalid block
    public void test_2() {
        byte[] rlp = Hex.decode("");
        Block b = new Block(rlp);
        assertFalse(PowHelper.isValid(b.getHeader()));
    }

    @Ignore
    @Test // stress test
    public void test_3() {
        int iterCnt = 1_000_000;

        byte[] rlp = Hex.decode("");
        Block b = new Block(rlp);
        long start = System.currentTimeMillis();
        for (int i = 0; i < iterCnt; i++)
            PowHelper.isValid(b.getHeader());

        long total = System.currentTimeMillis() - start;

        System.out.println(String.format("Time: total = %d ms, per block = %.2f ms", total, (double) total / iterCnt));
    }
}
